//fetch-user/me.posts
import "./post.scss";
import "../comments/comments.scss";
import FavoriteBorderOutlinedIcon from "@mui/icons-material/FavoriteBorderOutlined";
import FavoriteOutlinedIcon from "@mui/icons-material/FavoriteOutlined";
import TextsmsOutlinedIcon from "@mui/icons-material/TextsmsOutlined";
import ShareOutlinedIcon from "@mui/icons-material/ShareOutlined";
import MoreHorizIcon from "@mui/icons-material/MoreHoriz";
import { Link, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import * as userService from "../../services/user";
import * as postService from "../../services/post";
import Joi from "joi";

const Post = ({ post, currentUser }) => {
  const navigate = useNavigate();
  const [comments, setComments] = useState([]);
  const [commentOpen, setCommentOpen] = useState(false);
  const [users, setUsers] = useState([]);
  const [user, setUser] = useState({});
  //TEMPORARY
  const liked = false;

  useEffect(() => {
    userService.fetchUser(post.userId).then((response) => {
      setUser(response.data);
    });

    postService.fetchCommentsByPost(post.id).then((response) => {
      setComments(response.data);
    });

    userService.fetchUsers().then((response) => {
      setUsers(response.data);
    });
  }, []);

  const getNumberOfComments = () => {
    if (comments.length > 1) {
      return `${comments.length} Comments`;
    } else if (comments.length == 1) {
      return `${comments.length} Comment`;
    } else {
      return "Comment";
    }
  };

  const getImage = (id) => {
    const data = users.find((user) => user.id === id);
    return data["imageUrl"];
  };

  const getUsername = (id) => {
    const data = users.find((user) => user.id === id);
    return data["username"];
  };

  const [form, setForm] = useState({
    value: "",
    postId: post.id,
  });

  const [errors, setErrors] = useState({});

  /*form validation */
  const schema = Joi.object({
    value: Joi.string().required(),
    postId: Joi.string().required(),
  });

  const handleCommentSubmit = async (event) => {
    event.preventDefault();
    try {
      if (form.id) {
        postService
          .updateComment(form.postId, form.id, form.value)
          .then((response) => {
            postService.fetchCommentsByPost(post.id).then((response) => {
              setComments(response.data);
              setForm({
                ...form,
                id: "",
                value: "",
              });
            });
          });
      } else {
        await postService.addComment(form.postId, form.value);
        postService.fetchCommentsByPost(post.id).then((response) => {
          setComments(response.data);
        });
      }
      getNumberOfComments();
      setForm({
        ...form,
        value: "",
      });
    } catch (error) {
      if (error.response && error.response.status === 400) {
        alert(error.response.data.message);
      }
    }
  };

  const handleChange = ({ currentTarget: input }) => {
    setForm({
      ...form,
      [input.name]: input.value,
    });

    const { error } = schema
      .extract(input.name)
      .label(input.name)
      .validate(input.value);

    if (error) {
      setErrors({ ...errors, [input.name]: error.details[0].message });
    } else {
      delete errors[input.name];
      setErrors(errors);
    }
  };

  const handleCommentDelete = async (id, commentId) => {
    try {
      await postService.deleteComment(id, commentId).then((response) => {
        postService.fetchCommentsByPost(post.id).then((response) => {
          setComments(response.data);
        });
        getNumberOfComments();
      });
    } catch (error) {
      if (error.response && error.response.status === 404) {
        alert("Data might have already been deleted");
      }
    }
  };

  const isUserComment = (userId, postId, commentId, commentValue) => {
    if (userId === currentUser.id) {
      return (
        <>
          <a
            className="edit-ms"
            onClick={() => editComment(commentId, commentValue)}
          >
            Edit
          </a>
          <a
            aria-disabled="true"
            className="delete-ms"
            onClick={() => handleCommentDelete(postId, commentId)}
          >
            Delete
          </a>
        </>
      );
    }
  };

  /* button disabled = true or false */
  const isFormInvalid = () => {
    const result = schema.validate(form);
    return !!result.error;
  };

  const editComment = async (id, commentValue) => {
    setForm({
      ...form,
      value: commentValue,
      id: id,
    });
  };
  // const current = new Date();
  // const date = `${current.getDate()}/${
  //   current.getMonth() + 1
  // }/${current.getFullYear()}`;
  constructor();

  var today = new Date(),
    time =
      today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();

  this.state = {
    currentTime: time,
  };

  return (
    <div className="post">
      <div className="container">
        <div className="user">
          <div className="userInfo">
            {/* For profile of the post owner */}
            <img src={user.imageUrl} alt="" />{" "}
            <div className="details">
              <Link
                to={`/profile/${user.username}/posts`}
                style={{ textDecoration: "none", color: "inherit" }}
              >
                {/* For username of the post owner */}
                <span className="name">{user.username}</span>
              </Link>
              <span> {this.state.currentTime}</span>
            </div>
          </div>
          <Link
            to={`/posts/${post.id}`}
            style={{ textDecoration: "none", color: "inherit" }}
          >
            <MoreHorizIcon />
          </Link>
        </div>
        <div className="content">
          <p>{post.value}</p>
          <img src={post.postImageURL} alt="" />
        </div>
        <div className="info">
          <div className="item">
            {liked ? <FavoriteOutlinedIcon /> : <FavoriteBorderOutlinedIcon />}
            12 Likes
          </div>
          <div className="item" onClick={() => setCommentOpen(!commentOpen)}>
            <TextsmsOutlinedIcon />
            {getNumberOfComments()}
          </div>
        </div>

        {/* COmment Start */}
        {commentOpen && (
          <>
            <div className="comments">
              {/* Comment Form */}
              <form onSubmit={handleCommentSubmit} className="write">
                {/* <form> */}
                <img src={currentUser.imageUrl} alt="" />
                <input
                  name="postId"
                  onChange={handleChange}
                  value={form.postId}
                  type="hidden"
                />
                <input
                  name="value"
                  onChange={handleChange}
                  value={form.value}
                  type="text"
                  placeholder="write a comment"
                  required
                />
                {form.id ? (
                  <>
                    <input
                      name="id"
                      onChange={handleChange}
                      value={form.id}
                      type="hidden"
                      required
                    />
                    <button type="submit">Update</button>
                  </>
                ) : (
                  <button type="submit">Send</button>
                )}

                {/* </form> */}
              </form>
              {/* Comment Form */}
              {comments.map((comment) => (
                <div className="comment">
                  <img src={getImage(comment.userId)} alt="" />

                  <div className="info">
                    <span>{getUsername(comment.userId)}</span>
                    <p>{comment.value}</p>
                    <small className="edit-delete">
                      {isUserComment(
                        comment.userId,
                        comment.postId,
                        comment.id,
                        comment.value
                      )}
                    </small>
                  </div>

                  <span className="date"></span>
                </div>
              ))}
            </div>
          </>
        )}
        {/* COmment End */}
      </div>
    </div>
  );
};

export default Post;
